import React, {useEffect, useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import styles from "./styles.js"
import {
    Avatar,
    Card,
    CardActions,
    CardContent,
    CardHeader,
    Typography,
    CardMedia,
    Button,
    Box
} from "@material-ui/core";
const useStyles = makeStyles(styles);

function generate(element) {
    return ["step1: ", "step2: ", "step3: "].map((value) =>
        React.cloneElement(element, {
            key: value,
        }),
    );
}
function obj2Arr(data){
    let res=[];
    console.log("convert before",data);
    for (var i=0;i< data.length;i++){
        res+=[data[i]]
        console.log(typeof data[i])
    }
    console.log("converted is: ", res);
    return res

}
export default function Tutorial() {
    const classes = useStyles();
    var img;
    const [dense, setDense] = React.useState(false);

    const [state, setState] = useState({
        steps:null,
        imgUrl:null,
    })
    const [hasError, setHasError] = useState(false)
    const [loading, setLoading]= useState(false)

    useEffect(() => {
        setLoading(true)
        fetch("/api",{ mode: 'cors'}).then(res=>res.json()).then(
            (result) => {
                const steps=obj2Arr(result["setup"]["tutorial"]["steps"]);
                console.log("start setState");
                setState({
                    steps:steps,
                    imgUrl:result["setup"]["tutorial"]["imgUrl"]
                });
                setLoading(false);
            }
        ).catch(err => {
            setHasError(err)
        setLoading(false)})
},[])
    if (hasError!=false) {
        return <div>Error: {hasError.message}</div>;
    } else if (loading) {
        return <div>Loading...</div>;
    } else {
img=state.imgUrl;
        console.log("state from return is: ",state);

        return (
            <Box boxShadow={3}>
                <Card className={classes.root}>
                    <Box boxShadow={3}>
                        <CardHeader
                            className={classes.tutorialTitles}
                            title="Tutorial"
                            subheader="This short tutorial is meant to guide you through the annotation process. "
                        />
                    </Box>
                    <div className={classes.imageContainer}>
                        <CardMedia
                            className={classes.media}
                            image='https://geodata.lib.utexas.edu/assets/blac_featured_image_map-dca4cbca4e07010e5bd201ad8fab1cc0aa9abd24a8842f90e3c1eef1834e8498.jpg'
                            title="Paella dish"
                        />
                    </div>
                    <Box boxShadow={3}>
                        <CardContent>
                            <List dense={dense}>
                                {/*{generate(*/}
                                {/*    <ListItem>*/}
                                {/*        <ListItemText*/}
                                {/*            primary="Single-line item"*/}
                                {/*            secondary={secondary ? 'Secondary text' : null}*/}
                                {/*        />*/}
                                {/*    </ListItem>,*/}
                                {/*)}*/}
                                {
                                    // steps.map(function (step) {
                                    //     return <div>{step}</div>
                                    // })
                                }
                            </List>
                        </CardContent>
                    </Box>

                </Card>
            </Box>
        );
    }
}
